        public void Resolve(bool fromBattle = false)
        {
            if (Player.player.coins < 0)
                Player.player.coins = 0;
            Console.BackgroundColor = bgColor;
            Console.ForegroundColor = fgColor;
            Console.Clear();
            Program.EquipCheck();
            if (visited)
            {
                Program.Write("residince = " + name, 750, true);
                Program.Write("data = " + description, 750, true);
                Program.Write(image, 750, true);
            }
            else
            {
                Program.WriteSlow("residince = " + name, 50, 1, 500, true);
                if (this == locationXXIX || this == locationXXX)
                    Program.WriteSlow("data = " + description, 50, 1, 500, true, true);
                else
                    Program.WriteSlow("data = " + description, 50, 1, 500, true);
                Program.WriteSlow(image, 10, 20, 500, true);
            }
            Console.WriteLine();
            visited = true;
            if (Player.player.inventory.Contains(Item.map))
            {
                Console.WriteLine($"          {MapIcon(locationXI)}e      {MapIcon(locationXXX)}   {MapIcon(locationX)}e      {MapIcon(locationXXIX)}   {MapIcon(locationIX)}   {MapIcon(locationXXIII)}e      {MapIcon(locationIII)}   {MapIcon(locationVIII)}   {MapIcon(locationXIX)} {MapIcon(locationXX)} {MapIcon(locationXXI)}e{MapIcon(locationXXVI)} {MapIcon(locationXXV)} {MapIcon(locationXXIV)} {MapIcon(locationII)} {MapIcon(locationIV)} {MapIcon(locationV)} {MapIcon(locationXII)} {MapIcon(locationXIII)}   {MapIcon(locationXXII)}e  {MapIcon(locationXXVII)}   {MapIcon(locationI)}   {MapIcon(locationVI)}   {MapIcon(locationXIV)}e  {MapIcon(locationXXVIII)}       {MapIcon(locationVII)}   {MapIcon(locationXV)} {MapIcon(locationXVIII)}e      {MapIcon(locationXXXI)}       {MapIcon(locationXVI)}e      {MapIcon(locationXXXII)}       {MapIcon(locationXVII)}e".Replace("e", Environment.NewLine));
            }
            Program.options = new List<string> { };
            bool inRoom = true;
            Random rand = new Random();
            if (battles != null)
            {
                if (this == locationXXX)
                {
                    Enemy.you = new Enemy(Player.player.name, Player.player.exp, Player.player.hp, Player.player.coins, Player.player.stats);
                    Program.Write(".", 500, true, 400);
                    Program.Write(".", 500, true, 400);
                    Program.Write(".", 2000, true, 400);
                    Battle.finalBoss = true;
                    new Battle(new List<Enemy> { Enemy.you }).Resolve(Player.player);
                    Resolve(true);
                }
                if (battles[0].boss)
                {
                    Program.Write("!!BOSS!!", 300, true, 600);
                    Program.Write("!!BOSS!!", 300, true, 600);
                    Program.Write("!!BOSS!!", 1500, true, 600);
                    battles[0].Resolve(Player.player);
                    battles = null;
                    description = descriptionAlt;
                    Resolve(true);
                }
                else if (rand.Next(0, 2) == 0 && !fromBattle)
                {
                    Program.Write("!Encounter!", 2000, true);
                    int chosen = rand.Next(0, battles.Count);
                    battles[chosen].Resolve(Player.player);
                    if (!Player.flee) 
                        battles.Remove(battles[chosen]);
                    Player.flee = false;
                    if (battles.Count == 0)
                        battles = null;
                    Resolve(true);
                }
            }
            while (inRoom)
            {
                activeKey = null;
                DirectionCheck(north, "N");
                DirectionCheck(east, "E");
                DirectionCheck(south, "S");
                DirectionCheck(west, "W");
                Console.WriteLine();
                Console.WriteLine("P : Pockets");
                Program.options.Add("P");
                Console.WriteLine("F : Forage");
                Program.options.Add("F");
                if (name == "Home")
