                    Console.WriteLine();
                    Console.WriteLine("H : Help");
                    Program.options.Add("H");
                }
                else if (name == "Village househousegouse")
                {
                    Console.WriteLine();
                    Console.WriteLine("T : Talk");
                    Program.options.Add("T");
                }
                else if (name == "C7A7S7I7N7O")
                {
                    Console.WriteLine();
                    Console.WriteLine("G : Gamble");
                    Program.options.Add("G");
                }
                else if (name == "Meat Cube")
                {
                    Console.WriteLine();
                    Console.WriteLine("C : Commerse");
                    Program.options.Add("C");
                }
                else if (name == "Gas Station" || name == "T h e  B a c k")
                {
                    Console.WriteLine();
                    Console.WriteLine("B : Buy");
                    Program.options.Add("B");
                }
                else if (name == "Lab:")
                {
                    Console.WriteLine();
                    Console.WriteLine("L : Lab");
                    Program.options.Add("L");
                }
                else if (name == "UnKnown.")
                {
                    Console.WriteLine();
                    Console.WriteLine("??? : SOLVE HIS RIDDLE");
                    Program.options.Add("THE KING FORBIDS YOUR VISIT.");
                }
                else if (name == "Burger King")
                {
                    Console.WriteLine();
                    Console.WriteLine("! : whopper");
                    Program.options.Add("!");
                }
                Console.WriteLine();
                Location nextLocation;
                string choice = Program.InputString(Program.options, "ADVANCE ? TO", true);
                if (choice == "T")
                {
                    Console.WriteLine();
                    int d = rand.Next(0, villageDialogue.Length);
                    Console.WriteLine("CITIZEN#" + d + ": " + villageDialogue[d]);
                    if (d == 6 && !Player.player.recipes.Contains(Recipe.recipeI))
                        Player.player.recipes.Add(Recipe.recipeI);
                    Console.WriteLine();
                    Console.ReadLine();
                }
                else if (choice == "G")
                {
                    Console.WriteLine();
                    Console.WriteLine("Pick a - aPick a <MACHINE>");
                    for (int m = 0; m < Machine.machines.Length; m++)
                    {
                        Console.WriteLine($"{m} : {Machine.machines[m].name} ({Machine.machines[m].cost})");
                    }
                    Console.WriteLine("BALANCE" + Player.player.coins);
                    Machine.Gamble(Program.InputInt(0, Machine.machines.Length - 1));
                }
                else if (choice == "C")
                {
                    Console.WriteLine();
                    Program.Write("Pick two items of equal value to sacrafice.", 0, true);
                    Item[] offerings = Program.InvChooseTwo();
                    if (offerings[0].value == offerings[1].value && !(offerings[0] == offerings[1] && Player.player.inventory.Where(s => s != null && s == offerings[0]).Count() < 2))
                    {
                        List<Item> options = new List<Item> { };
                        foreach (Item item in Item.inventoryOrder)
                        {
                            if (item != null)
                                if (item.value == offerings[0].value + 1)
                                    options.Add(item);
                        }
                        Item output = options[rand.Next(0, options.Count)];
                        Program.Write("Worthy offer. Take this " + output.name + ".", 0, true);
                        Player.player.inventory.Remove(offerings[0]);
                        Player.player.inventory.Remove(offerings[1]);
                        Player.player.inventory.Add(output);
                    }
                    else
                    {
                        Console.WriteLine("Unacceptable.");
                    }
                    Console.ReadLine();
                }
                else if (choice == "P")
                {
                    Console.WriteLine();
                    Console.WriteLine("_Pocket-Contents_");
                    Console.WriteLine("BALANCE" + Player.player.coins);
                    Console.WriteLine("health!" + Program.Bar(Player.player.stats.maxHP, Player.player.hp, 10));
                    Console.WriteLine("LEVEL." + Player.player.level + ": " + Program.Bar(Player.player.maxExp, Player.player.exp, 10));
                    Console.WriteLine("ATK)>." + Player.player.stats.attack + " + " + Player.player.weapon.damage);
                    Console.WriteLine("DEF))." + Player.player.stats.defence + " + " + Player.player.armor.defense);
                    Console.WriteLine();
                    Program.GetInventory();
                }
                else if (choice == "F")
                {
                    int forage = rand.Next(0, 10);
                    if (forage < 5 && battles != null)
                    {
                        if (name == "bonusBonusBONUSbonusBObus")
                            battles.Add(new Battle(new List<Enemy> { coinEnemies[rand.Next(0, 4)], coinEnemies[rand.Next(0, 4)], coinEnemies[rand.Next(0, 4)] }));
                        Program.Write("!Encounter!", 2000, true);
                        int chosen = rand.Next(0, battles.Count);
                        battles[chosen].Resolve(Player.player);
                        if (!Player.flee)
                            battles.Remove(battles[chosen]);
                        Player.flee = false;
                        if (battles.Count == 0)
                            battles = null;
                        Resolve(true);
                    }
                    else if (forage < 7 && coins != null)
                    {
                        int chosen = rand.Next(0, coins.Count);
                        Program.Write("F$OUN$D " + coins[chosen], 0, true);
                        Player.player.coins += coins[chosen];
                        coins.Remove(coins[chosen]);
                        if (coins.Count == 0)
                            coins = null;
                    }
                    else if (forage < 9 && items != null)
                    {
                        int chosen = rand.Next(0, items.Count);
                        Program.Write("F!OUN!D " + items[chosen].name, 0, true);
                        Player.player.inventory.Add(items[chosen]);
                        items.Remove(items[chosen]);
                        if (items.Count == 0)
                            items = null;
                    }
                    else if (recipe != null)
                    {
                        Program.Write("found RECIPE for :  " + recipe.outgredient.name, 0, true);
                        Player.player.recipes.Add(recipe);
                        recipe = null;
                    }
                    else
                    {
                        Console.WriteLine("NOT.");
                    }
                }
                else if (choice == "H")
                {
                    Program.WriteSlow("╔═══════════════════════╗\r\n║NOTES you NEED to know!║\r\n║      ^(yes, you)      ║\r\n╠═══════════════════════╣\r\n║FORAGE to find \"things\"║\r\n║⋅moneyy$$$$$$$$$$$$$$$$║\r\n║⋅mmmmthingss of sorts??║\r\n║⋅                      ║\r\n║⋅recipes ohh hh recipes║\r\n║⋅!Encounter!!Encounter!║\r\n║(LIMITED SUPPLIES!!!!!)║\r\n║    (^usually(usually))║\r\n╠═══════════════════════╣\r\n║SOME items may be::::::║\r\n║ needed to progress(!) ║\r\n╠═══════════════════════╣\r\n║...Information from ...║\r\n║...     VILLAGE FOLK...║\r\n║...may be needed    ...║\r\n╚═══════════════════════╝", 10, 5, 1000, true);
                }
                else if (choice == "B")
                {
                    if (name == "Gas Station")
                    {
                        Shop.gasStation.Resolve(fgColor);
                    }
                    else
                    {
                        Shop.theBack.Resolve(fgColor);
                    }
                    Resolve(true);
                }
                else if (choice == "L")
                {
                    Recipe.Resolve();
                    Resolve(true);
                }
                else if (choice == "!")
                {
                    if (Player.player.inventory.Contains(Item.whopper))
                    {
                        Program.WriteSlow("You can't have more than one.", 200, 1, 500, true);
                        for (int i = 0; i < 100; i++)
                        {
                            Console.Write(Program.Garble(1000));
                            Console.Beep(100, 50);
                        }
                        locationI.Resolve();
                    }
                    else
                    {
                        Player.player.inventory.Add(Item.whopper);
                        Program.Write("whopper", 0, true);
                        whopp = true;
                    }
                }
                else if (choice == "THE KING FORBIDS YOUR VISIT.")
                    locationXXXII.Resolve();
                else
                {
                    if (choice == "N")
                    {
                        nextLocation = north;
                    }
                    else if (choice == "E")
                    {
                        nextLocation = east;
                    }
                    else if (choice == "S")
                    {
                        nextLocation = south;
                    }
                    else
                    {
                        nextLocation = west;
                    }
                    inRoom = false;
                    nextLocation.Resolve();
                }
            }
        }

        public string MapIcon(Location location)
        {
            if (this == location)
            {
                return "▓";
            }
            else if (location == locationXXXI || location == locationXXXII)
            {
                return " ";
            }
            else if (location.locked)
            {
                return "X";
            }
            else if (location.visited)
            {
                return "░";
            }
            else
            {
                return "?";
            }
        }

        public void DirectionCheck(Location dir, string letter)
        {
            if (!(dir is null))
            {
                if (dir.locked)
                {
                    Console.WriteLine(letter + " : INACESSIBLE");
                    activeKey = dir.key;
                    lockedRoom = dir;
                }
                else
                {
                    if (dir.visited)
                    {
                        Console.WriteLine(letter + " : " + dir.name);
                    }
                    else
                    {
                        Console.WriteLine(letter + " : ???");
                    }
                    Program.options.Add(letter);
                }
            }
        }
    }
